% Diagnol (Matrix form)
function [D] = diagnol_matrix(Ux,Uy,Uz,grid)
c = grid.c;
gamma = sqrt(1 + (Ux*Ux+Uy*Uy+Uz*Uz)/(c*c));
T = grid.temp;
D =  [[(Ux*(Ux^2 + Uy^2 + Uz^2 + c^2))/(c^2*gamma^3),                                             0,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              0,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              0];...
    [                                            0, (Ux*(Ux^2 + Uy^2 + Uz^2 + c^2))/(c^2*gamma^3),                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              0,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              0];...
    [                                            0,                                             0, (Ux*Uy^2 - T*Ux + Ux*Uz^2 + Ux*c^2 + (T^2*Ux^2 + 2*T*Ux^4 + 6*T*Ux^2*Uy^2 + 6*T*Ux^2*Uz^2 - 2*T*Ux^2*c^2*gamma^2 + 6*T*Ux^2*c^2 + 4*T*Uy^4 + 8*T*Uy^2*Uz^2 + 8*T*Uy^2*c^2 + 4*T*Uz^4 + 8*T*Uz^2*c^2 + 4*T*c^4 + Ux^6 - 2*Ux^4*Uy^2 - 2*Ux^4*Uz^2 - 2*Ux^4*c^2*gamma^2 - 2*Ux^4*c^2 - 3*Ux^2*Uy^4 - 6*Ux^2*Uy^2*Uz^2 + 2*Ux^2*Uy^2*c^2*gamma^2 - 6*Ux^2*Uy^2*c^2 - 3*Ux^2*Uz^4 + 2*Ux^2*Uz^2*c^2*gamma^2 - 6*Ux^2*Uz^2*c^2 + Ux^2*c^4*gamma^4 + 2*Ux^2*c^4*gamma^2 - 3*Ux^2*c^4)^(1/2) + Ux^3 + Ux*c^2*gamma^2)/(2*c^2*gamma^3),                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              0];...
    [                                            0,                                             0,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              0, (Ux*Uy^2 - T*Ux + Ux*Uz^2 + Ux*c^2 - (T^2*Ux^2 + 2*T*Ux^4 + 6*T*Ux^2*Uy^2 + 6*T*Ux^2*Uz^2 - 2*T*Ux^2*c^2*gamma^2 + 6*T*Ux^2*c^2 + 4*T*Uy^4 + 8*T*Uy^2*Uz^2 + 8*T*Uy^2*c^2 + 4*T*Uz^4 + 8*T*Uz^2*c^2 + 4*T*c^4 + Ux^6 - 2*Ux^4*Uy^2 - 2*Ux^4*Uz^2 - 2*Ux^4*c^2*gamma^2 - 2*Ux^4*c^2 - 3*Ux^2*Uy^4 - 6*Ux^2*Uy^2*Uz^2 + 2*Ux^2*Uy^2*c^2*gamma^2 - 6*Ux^2*Uy^2*c^2 - 3*Ux^2*Uz^4 + 2*Ux^2*Uz^2*c^2*gamma^2 - 6*Ux^2*Uz^2*c^2 + Ux^2*c^4*gamma^4 + 2*Ux^2*c^4*gamma^2 - 3*Ux^2*c^4)^(1/2) + Ux^3 + Ux*c^2*gamma^2)/(2*c^2*gamma^3)]];
end


% A_single (Matrix form)
function [A] = A_single(Ux,Uy,Uz,grid)
c = grid.c;
gamma = sqrt(1 + (Ux*Ux+Uy*Uy+Uz*Uz)/(c*c));
T = grid.temp;
A =  [ [                            (Ux*(Ux^2 + Uy^2 + Uz^2))/(c^2*gamma^3),                        (Uy^2 + Uz^2 + c^2)/(c^2*gamma^3),                 -(Ux*Uy)/(c^2*gamma^3),                 -(Ux*Uz)/(c^2*gamma^3)];...
    [(- Ux^2*c^2 + 2*T*Ux^2 + 2*T*Uy^2 + 2*T*Uz^2 + T*c^2)/(c^2*gamma^3), (Ux*(Uy^2 + Uz^2 + c^2*gamma^2 + c^2 - T))/(c^2*gamma^3),         -(Uy*(Ux^2 + T))/(c^2*gamma^3),         -(Uz*(Ux^2 + T))/(c^2*gamma^3)];...
    [                                                   -(Ux*Uy)/gamma^3,                   (Uy*(Uy^2 + Uz^2 + c^2))/(c^2*gamma^3), (Ux*(Ux^2 + Uz^2 + c^2))/(c^2*gamma^3),              -(Ux*Uy*Uz)/(c^2*gamma^3)];...
    [                                                   -(Ux*Uz)/gamma^3,                   (Uz*(Uy^2 + Uz^2 + c^2))/(c^2*gamma^3),              -(Ux*Uy*Uz)/(c^2*gamma^3), (Ux*(Ux^2 + Uy^2 + c^2))/(c^2*gamma^3)]];
end

function [W_plus, W_minus] = edges_linear(W_tilde,dW)
W_plus = W_tilde - dW/2;
W_minus = W_tilde + dW/2;
end


%Sup function
function [sup_val] = sup(values)
[maxB, index] = max(abs(values));
sup_val = maxB * sign(values(index));
end


%Check that X is real and isnotnan
function check(grid,X,str)

% Run only at select iterations:
if str == "R" || (mod ( grid.iter, grid.Output_interval ) == 0 || grid.iter == grid.NT)

    %Stop if isreal fails
    if (~isreal(X))
        fprintf("FAILS: Not-Real Soln.\n");
        fprintf("STR: %s\n",str);
        pause(1000)
    end

    %Stop if isnan fails
    if max(max((isnan(X))))
        fprintf("FAILS: Nan Soln.\n");
        fprintf("STR: %s\n",str);
        pause(1000)
    end

    %Stop if isfinite fails
    if min(min((isfinite(X)))) == 0
        fprintf("FAILS: Inf Soln.\n");
        fprintf("STR: %s\n",str);
        pause(1000)
    end

end
